"use strict";(self.webpackChunkdocs=self.webpackChunkdocs||[]).push([[900],{8936:r=>{r.exports=JSON.parse('{"blogPosts":[{"id":"/20230602","metadata":{"permalink":"/formalizedChinese/blog/20230602","source":"@site/blog/20230602.md","title":"\u82f1\u8bed\u5b66\u4e60\u7684\u8ba4\u77e5\u89c2","description":"\u8ba4\u77e5\u82f1\u8bed","date":"2023-06-02T02:18:27.221Z","formattedDate":"2023\u5e746\u67082\u65e5","tags":[],"readingTime":4.855,"hasTruncateMarker":false,"authors":[],"frontMatter":{},"nextItem":{"title":"\u5f62\u5f0f\u4e2d\u6587\uff0c\u7a0b\u5e8f\u5458\u89c6\u89d2\u4e0b\u7684\u82f1\u8bed\u5b66\u4e60","permalink":"/formalizedChinese/blog/20230531"}},"content":"## \u8ba4\u77e5\u82f1\u8bed\\r\\n**\u60f3\u6e05\u695a\u4e3a\u4ec0\u4e48\u5b66\u82f1\u8bed\u6bd4\u600e\u4e48\u5b66\u82f1\u8bed\u66f4\u91cd\u8981**\uff0c\u5728\u5e94\u8bd5\u82f1\u8bed\u6559\u80b2\u7684\u5f71\u54cd\u4e0b\uff0c\u8bb8\u591a\u4eba\u5bf9\u82f1\u8bed\u7684\u8ba4\u77e5\u662f\u626d\u66f2\u7684\u3002\u5206\u6570\u5bfc\u5411\uff0c\u626d\u66f2\u4e86\u8bed\u8a00\u5b66\u4e60\u672c\u8d28\u3002\u629b\u5f00\u5e94\u8bd5\u6559\u80b2\u7684\u8bc4\u4ef7\u4f53\u7cfb\uff0c\u82f1\u8bed\u5b66\u4e60\u672c\u8d28\u662f\u4e3a\u4e86\u8868\u8fbe\uff0c\u5b66\u597d\u610f\u601d\u662f\u7528\u597d\uff0c\u4e5f\u5c31\u662f\u66f4\u6e05\u6670\u3001\u51c6\u786e\u4e14\u6709\u903b\u8f91\u5730\u8868\u8fbe\uff0c\u4e3a\u4ec0\u4e48\u8bf4\u662f\u4e3a\u4e86\u8868\u8fbe\uff0c**All for \u8868\u8fbe**\uff0c\u901a\u5e38\u60f3\u5230\u7684\u662f\u5199\u4f5c\u6216\u4ea4\u6d41\uff0c\u8fd9\u662f\u72ed\u4e49\u7684\u8868\u8fbe\\r\\n\\r\\n\u5bf9\u7a0b\u5e8f\u5458\u6765\u8bf4\uff0c\u5199\u4ee3\u7801\u4e5f\u662f\u8868\u8fbe\uff0c\u53ea\u662f\u8868\u8fbe\u9762\u5411\u7684\u5bf9\u8c61\u662f\u8ba1\u7b97\u673a\u6216\u5176\u4ed6\u7a0b\u5e8f\u5458\u3002\u6240\u4ee5\u5b66\u82f1\u8bed\u7684\u76ee\u6807\u5c31\u5f88\u660e\u786e\u4e86\uff0c\u66f4\u597d\u5730\u8868\u8fbe\uff0c\u53ea\u6709\u8fd9\u4e2a\u76ee\u6807\u624d\u80fd\u4e0d\u65ad\u5730\u9a71\u4f7f\u4f60\u4e0d\u65ad\u5730\u5b66\u4e60\u82f1\u8bed\\r\\n\\r\\n\u4f60\u7684\u9605\u8bfb\u662f\u7ed9\u8868\u8fbe\u670d\u52a1\u7684\uff0c\u65e0\u8bba\u4f60\u5728\u8bfb\u8d44\u6599\u8fd8\u662f\u8bfb\u4ee3\u7801\uff0c\u66f4\u597d\u5730\u8868\u8fbe\u65e2\u662f\u8ba9\u5176\u4ed6\u4eba\u66f4\u9ad8\u6548\u5730\u7406\u89e3\u4f60\u8868\u8fbe\u7684\u610f\u601d\uff0c\u6c9f\u901a\u534f\u52a9\u66f4\u9ad8\u6548\uff0c\u4e5f\u662f\u5199\u51fa\u66f4\u597d\u7684\u4ee3\u7801\uff0c\u8ba9\u5176\u4ed6\u7a0b\u5e8f\u5458\u66f4\u5bb9\u6613\u7406\u89e3\u4f60\u7684\u4ee3\u7801\uff0c\u8fd9\u4e9b\u5bf9\u4f60\u6765\u8bf4\uff0c\u90fd\u662f**\u6b63\u9762\u6fc0\u52b1**\uff0c\u6b63\u9762\u6fc0\u52b1\u975e\u5e38\u91cd\u8981\uff0c\u5b83\u9a71\u4f7f\u4f60\u53bb\u505a\u66f4\u591a\u7684\u9605\u8bfb\u3001\u8868\u8fbe\u6765\u8fbe\u6210\u66f4\u597d\u7684\u8868\u8fbe\uff0c\u5f62\u6210\u5b66\u4e60\u95ed\u73af\u3002\u6b63\u9762\u6fc0\u52b1\u589e\u5f3a\u4f60\u5bf9\u82f1\u8bed\u7684\u8ba4\u53ef\uff0c\u52a0\u5f3a\u4fe1\u5ff5\u611f\uff0c\u4e5f\u5c31\u662f**\u4f60\u76f8\u4fe1\u4f60\u5728\u505a\u7684\u4e8b\uff0c\u5e76\u4e14\u76f8\u4fe1\u81ea\u5df1\u80fd\u505a\u597d**\\r\\n\\r\\n\u4e3a\u4ec0\u4e48\u8bf4\u6b63\u9762\u6fc0\u52b1\u975e\u5e38\u91cd\u8981\uff1f\u4f60\u60f3\u8fc7\u4f60\u4e3a\u4ec0\u4e48\u5b66\u4e0d\u597d\u82f1\u8bed\u5417\uff1f\u5b66\u4e0d\u597d\u6700\u672c\u8d28\u662f\u4f60\u5185\u5fc3\u6839\u672c\u5c31\u4e0d\u76f8\u4fe1\u4f60\u80fd\u5b66\u597d\uff0c\u8fd9\u79cd\u4e0d\u76f8\u4fe1\u53ef\u80fd\u4e0d\u662f\u4f60\u4e00\u5f00\u59cb\u5c31\u4e0d\u76f8\u4fe1\u7684\uff08\u5982\u679c\u4e00\u5f00\u59cb\u662f\u5c31\u66f4\u60e8\u4e86\uff09\uff0c\u800c\u5b66\u4e60\u8fc7\u7a0b\u4e2d\u7684\u8d1f\u53cd\u9988\uff0c\u4e00\u70b9\u4e00\u70b9\u5730\u79ef\u7d2f\u5f62\u6210\u7684\uff0c\u6bd4\u5982\u8bf4\u80cc\u5355\u8bcd\uff0c\u80cc\u5927\u91cf\u5355\u8bcd\u51fa\u73b0\u7684\u9057\u5fd8\u73b0\u8c61\u8fd9\u79cd\u8d1f\u9762\u53cd\u9988\u5c31\u4f1a\u662f\u4f7f\u4f60\u4fe1\u5fc3\u53d7\u632b\uff0c\u5982\u4f1a\u8ba4\u4e3a\u81ea\u5df1\u7684\u8bb0\u5fc6\u529b\u5dee\uff0c\u6000\u7591\u81ea\u5df1\uff0c\u5426\u5b9a\u81ea\u5df1\uff0c\u7136\u800c\u4f60\u5fc5\u987b\u9762\u5bf9\u8003\u8bd5\u7684\u538b\u529b\uff0c\u4e0d\u5f97\u4e0d\u5728\u6295\u5165\u66f4\u591a\u65f6\u95f4\u80cc\u5355\u8bcd\uff0c\u6076\u6027\u5faa\u73af\uff0c\u7136\u540e\u5c31\u6162\u6162\u5730\u4e0d\u8ba4\u53ef\u81ea\u5df1\u5728\u505a\u7684\u4e8b\\r\\n\u9762\u5bf9\u9057\u5fd8\u7684\u95ee\u9898\u65f6\u5019\uff0c\u4e0d\u5e94\u8be5\u9003\u907f\u5b83\uff0c\u800c\u662f\u6b63\u89c6\u5b83\uff0c\u4f60\u60f3\u8fc7\u4e3a\u4ec0\u4e48\u80cc\u5b8c\u5355\u8bcd\u4f1a\u53c8\u5fd8\u4e86\u5417\uff1f\u5fd8\u8bb0\u4e0d\u662f\u56e0\u4e3a\u4f60\u8bb0\u5fc6\u529b\u5dee\uff0c\u800c\u662f\u5355\u8bcd\u5bf9\u4f60\u6765\u8bf4\u4e0d\u63a5\u53d7\u3001\u4e0d\u91cd\u8981\u3001\u6ca1\u8054\u7cfb\uff0c\u80cc\u5355\u8bcd\u5f88\u91cd\u8981\u8fd9\u79cd\u8bf4\u6cd5\u53ea\u662f\u8001\u5e08\u3001\u8003\u8bd5\u7b49\u5916\u5728\u56e0\u7d20\u5f3a\u8feb\u4f60\u7684\uff0c\u4f60\u5185\u5fc3\u4ece\u6765\u5c31\u4e0d\u89c9\u5f97\u5355\u8bcd\u5f88\u91cd\u8981\uff0c\u56e0\u4e3a\u4f60\u5728\u5fd8\u8bb0\u3002\u6216\u8005\u8bf4\u8bb0\u5355\u8bcd\u8fd9\u4ef6\u4e8b\u5f88\u75db\u82e6\uff0c\u53ef\u5fd8\u8bb0\u75db\u82e6\u662f\u4eba\u7c7b\u7684\u81ea\u6211\u4fdd\u62a4\u673a\u5236\uff0c\u4e0d\u8981\u4e5f\u4e0d\u53ef\u80fd\u8fdd\u80cc\u4eba\u7684\u5929\u6027\uff0c\u76f4\u9762\u771f\u5b9e\u7684\u81ea\u5df1\u5427\\r\\n\\r\\n## \u5f62\u5f0f\u4e2d\u6587\\r\\n\u82f1\u8bed\u5b66\u4e60\u7684\u7b56\u7565\u53ef\u4ee5\u603b\u7ed3\u4e3a**\u63a5\u53d7\u3001\u91cd\u8981\u3001\u6709\u8054\u7cfb\u3001\u6b63\u9762\u6fc0\u52b1**\u3002\u8fd9\u4e9b\u7b56\u7565\u4e5f\u4e0d\u662f\u4e00\u8e74\u800c\u5c31\u7684\uff0c\u63a5\u53d7\u662f\u9010\u6e10\u63a5\u53d7\u7684\u8fc7\u7a0b\uff0c\u4f46\u9996\u5148\u6700\u91cd\u8981\u7684\u7b2c\u4e00\u4e2a\u4fe1\u5ff5\u5c31\u662f**\u76f8\u4fe1\u81ea\u5df1\u80fd\u5b66\u597d\u82f1\u8bed**\\r\\n\\r\\n\u9488\u5bf9\u82f1\u8bed\u5b66\u4e60\u7684\u73b0\u72b6\uff0c\u6211\u63d0\u51fa\u4e86\u5f62\u5f0f\u4e2d\u6587\u8fd9\u4e00\u4e2d\u82f1\u7ed3\u5408\u7684\u6e10\u8fdb\u5f0f\u6846\u67b6\u3002\u5f62\u5f0f\u4e2d\u6587\u662f\u4e00\u79cd\u9002\u7528\u4e8e\u5b66\u4e60\u82f1\u8bed\u7684\u5de5\u5177\u3002\u8fd9\u79cd\u5de5\u5177\u901a\u8fc7\u9650\u5b9a\u82f1\u8bed\u5b66\u4e60\u8303\u56f4\u5230\u8bfb\u5199\uff08read and write\uff09\uff0c\u5e76\u5141\u8bb8\u4e2d\u6587\u8bcd\u6c47\u8fdb\u884c\u5f62\u5f0f\u53d8\u5316\uff0c\u6765\u7b80\u5316\u82f1\u8bed\u5b66\u4e60\u8fc7\u7a0b\\r\\n\\r\\n\u5f62\u5f0f\u4e2d\u6587\u63a5\u53d7\u4f60\u4e0d\u8bb0\u5f97\u5355\u8bcd\uff0c\u8ba4\u4e3a\u8868\u8fbe\u5f88\u91cd\u8981\uff0c\u8ddf\u5df2\u6709\u77e5\u8bc6\u662f\u6709\u8054\u7cfb\u7684\uff0c\u4f7f\u7528\u8fed\u4ee3\u8868\u8fbe\u65b9\u5f0f\u7ed9\u5230\u6b63\u9762\u6fc0\u52b1\u3002\u5f53\u7136\u6b63\u9762\u6fc0\u52b1\u7528\u8fed\u4ee3\u8868\u8fbe\u8fd8\u4e0d\u591f\uff0c\u6240\u4ee5\u4f60\u60f3\u5b66\u597d\u82f1\u8bed\u5e76\u8ba4\u53ef\u5f62\u5f0f\u4e2d\u6587\u7684\u7406\u5ff5\uff0c\u6b22\u8fce\u52a0\u5165\u5f62\u5f0f\u4e2d\u6587\u7684\u793e\u533a\u5f62\u6210\u66f4\u591a\u7684\u6b63\u9762\u6fc0\u52b1\\r\\n\\r\\n[\u793e\u533aGithub Discussions](https://github.com/ishare20/formalizedChinese/discussions)"},{"id":"/20230531","metadata":{"permalink":"/formalizedChinese/blog/20230531","source":"@site/blog/20230531.md","title":"\u5f62\u5f0f\u4e2d\u6587\uff0c\u7a0b\u5e8f\u5458\u89c6\u89d2\u4e0b\u7684\u82f1\u8bed\u5b66\u4e60","description":"\u7a0b\u5e8f\u5458\u5c31\u5e94\u8be5\u7528\u7a0b\u5e8f\u5458\u7684\u7406\u5ff5\u53bb\u5b66\u82f1\u8bed\uff0c\u600e\u4e48\u8bf4\u5462\uff1f","date":"2023-05-31T07:46:14.000Z","formattedDate":"2023\u5e745\u670831\u65e5","tags":[],"readingTime":3.88,"hasTruncateMarker":false,"authors":[],"frontMatter":{},"prevItem":{"title":"\u82f1\u8bed\u5b66\u4e60\u7684\u8ba4\u77e5\u89c2","permalink":"/formalizedChinese/blog/20230602"}},"content":"\u7a0b\u5e8f\u5458\u5c31\u5e94\u8be5\u7528\u7a0b\u5e8f\u5458\u7684\u7406\u5ff5\u53bb\u5b66\u82f1\u8bed\uff0c\u600e\u4e48\u8bf4\u5462\uff1f\\r\\n\\r\\n\u5148\u770b\u73b0\u72b6\uff0c\u73b0\u5b9e\u4e2d\u7684\u5927\u591a\u6570\u7a0b\u5e8f\u5458\u53ea\u9700\u8981\u8bfb\u5199\uff08read and write\uff09\u80fd\u529b\uff0c\u6240\u4ee5\u6211\u4eec\u53ef\u4ee5\u628a\u82f1\u6587\u5b66\u4e60\u8303\u56f4\u7f29\u5c0f\u5230\u8bfb\u5199\uff0c\u4ece\u800c\u964d\u4f4e\u638c\u63e1\u82f1\u6587\u8bfb\u5199\u7684\u590d\u6742\u5ea6\u3002\u89e3\u51b3\u95ee\u9898\u65f6\u7f29\u5c0f\u6c42\u89e3\u8303\u56f4\uff0c\u5c31\u80fd\u964d\u4f4e\u6c42\u89e3\u7684\u590d\u6742\u5ea6\uff0c\u8fd9\u662f\u7a0b\u5e8f\u5458\u7684\u57fa\u672c\u5171\u8bc6\u3002\u6240\u4ee5\u6211\u63d0\u51fa\u4e86**\u5f62\u5f0f\u4e2d\u6587**\u8fd9\u4e00\u4e2d\u82f1\u7ed3\u5408\u7684\u6e10\u8fdb\u5f0f\u6846\u67b6\\r\\n\\r\\n[\u5f62\u5f0f\u4e2d\u6587\uff08Formalized Chinese\uff09](/docs/intro)\u662f\u4e00\u79cd\u9002\u7528\u4e8e\u7a0b\u5e8f\u5458\u5b66\u4e60\u82f1\u8bed\u7684\u5de5\u5177\u3002\u8fd9\u79cd\u5de5\u5177\u901a\u8fc7\u9650\u5b9a\u82f1\u8bed\u5b66\u4e60\u8303\u56f4\u5230\u8bfb\u5199\uff08read and write\uff09\uff0c\u5e76\u5141\u8bb8\u4e2d\u6587\u8bcd\u6c47\u8fdb\u884c\u5f62\u5f0f\u53d8\u5316\uff0c\u6765\u7b80\u5316\u82f1\u8bed\u5b66\u4e60\u8fc7\u7a0b\\r\\n\\r\\n\u4f8b\u6587\\r\\n\\r\\nEnglish is \u5f62\u5408\u7684er than\xa0Chinese. It\xa0is \u8868\u73b0ed in \u4ee5\u4e0b\u7684 3\u70b9:\\r\\n\\r\\n1. A \u53e5\u5b50 \u901a\u5e38ly has a \u5b8c\u6574\u7684 \u7ed3\u6784. Its \u8bed\u6cd5\u6210\u5206 such as \u4e3b\u8bed, \u8c13\u8bed or others can\'t be \u7f3a\u5931\u7684. The \u5173\u7cfb between \u4e3b\u53e5 and \u4ece\u53e5 is \u8868\u73b0ed by \u8fde\u63a5\u8bcds which \u5305\u62ec if, so, but and so on.\\r\\n\\r\\n2. There are \u5e9e\u5927\u6570\u91cf\u7684 \u5355\u8bcds with \u591a\u79cd\u7684 \u5f62\u6001\u53d8\u5316s in English. It is \u5fc5\u8981\u7684 to \u533a\u5206 \u65f6\u6001, \u8bcd\u6027 and so on of \u5355\u8bcds by their \u5f62\u5f0fs.\\r\\n\\r\\n3. English is \u5f71\u54cded by \u5f62\u5f0f\u903b\u8f91 of \u4e9a\u91cc\u58eb\u591a\u5fb7 and \u6587\u5316 of \u7406\u6027\u4e3b\u4e49. It is \u8981\u6c42ed to \u8868\u8fbe \u51c6\u786ely and \u903b\u8f91ly.\\r\\n\\r\\n\u5728\u5f62\u5f0f\u4e2d\u6587\u4e2d\uff0c\u82f1\u8bed\u5355\u8bcd\u548c\u4e2d\u6587\u8bcd\u8bed\u90fd\u88ab\u89c6\u4e3a\u7b26\u53f7\uff0c\u90fd\u53ef\u5f62\u5f0f\u53d8\u5316\\r\\n\\r\\n\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u7684\u8bbe\u5b9a\u5462\uff1f\\r\\n\\r\\n\u4ece\u6211\u4eec\u719f\u6089\u7684\u7f16\u7a0b\u8bed\u8a00\u770b\uff0c\u53d8\u91cf\u540d\u6216\u65b9\u6cd5\u540d\u4e0d\u8fc7\u662f\u6807\u8bc6\u7b26(Identifier)\uff0c\u7528\u4e2d\u6587\u65e5\u6587\u97e9\u6587\u7b49\u4efb\u4f55\u4e00\u4e2a\u56fd\u5bb6\u7684\u7b26\u53f7\u90fd\u53ef\u4ee5\uff0c\u53ea\u8981\u7b26\u5408\u8be5\u7f16\u7a0b\u8bed\u8a00\u53d8\u91cf\u540d\u6216\u65b9\u6cd5\u540d\u7684\u89c4\u5219\u5373\u53ef\u3002\u5e73\u65f6\u4f60\u53ea\u7528\u82f1\u6587\u4f5c\u4e3a\u6807\u8bc6\u7b26\uff0c\u53ea\u662f\u4e00\u79cd\u89c4\u8303\uff0c\u89c4\u8303\u4e0d\u7b49\u4e8e\u7edd\u5bf9\\r\\n\u653e\u5728\u82f1\u8bed\u4e0a\uff0c\u540d\u8bcd\u3001\u52a8\u8bcd\u3001\u5f62\u5bb9\u8bcd\u3001\u526f\u8bcd\u4e5f\u53ea\u662f\u6807\u8bc6\u7b26(Identifier)\u800c\u5df2\uff0c\u6807\u8bc6\u7b26\u4e0d\u4e00\u5b9a\u662f\u82f1\u6587\u7b26\u53f7\uff0c\u5982\u679c\u6807\u8bc6\u7b26\u662f\u4e2d\u6587\u5462\uff0c\u7b26\u5408\u6807\u8bc6\u7b26\u89c4\u5219\u5373\u53ef\\r\\n\\r\\n\u6211\u4eec\u8bc4\u4ef7\u662f\u5426\u638c\u63e1\u67d0\u4e2a\u7f16\u7a0b\u8bed\u8a00\u65f6\uff0c\u4e0d\u662f\u8bf4\u61c2\u5f88\u591a\u6807\u8bc6\u7b26\u7684\u547d\u540d\uff0c\u800c\u662f\u8bf4\u4f1a\u7528\u5c11\u91cf\u4fdd\u7559\u5173\u952e\u8bcd\u548c\u8bed\u6cd5\u89c4\u5219\u8868\u8fbe\u4e1a\u52a1\u903b\u8f91\uff0c\u540c\u6837\u7684\u9053\u7406\uff0c\u5f62\u5f0f\u4e2d\u6587\u7684\u89c2\u70b9\u8ba4\u4e3a\uff0c\u638c\u63e1\u82f1\u6587\u5c31\u662f\u4f1a\u7528\u5c11\u91cf\u4fdd\u7559\u5173\u952e\u8bcd\u548c\u8bed\u6cd5\u89c4\u5219\u8868\u8fbe\u610f\u601d\\r\\n\\r\\n\u90a3\u5982\u4f55\u7528\u597d\u82f1\u8bed\u5462\uff1f\\r\\n\\r\\n\u4e3a\u4ec0\u4e48\u4e0d\u8bf4\u5b66\u597d\u5462\uff1f\u8bed\u8a00\u4e60\u5f97\u6ca1\u6709\u5b66\u4f1a\u53ea\u6709\u7528\u4f1a\uff0c\u5f62\u5f0f\u4e2d\u6587\u63d0\u5021\u4ee5\u7528\u4ee3\u5b66\u3002\u5177\u4f53\u600e\u4e48\u64cd\u4f5c\u5462\uff1f\\r\\n\\r\\n[**\u8fed\u4ee3\u8868\u8fbe**](/docs/iteration)\\r\\n\\r\\n\u8fd9\u662f\u5f62\u5f0f\u4e2d\u6587\u975e\u5e38\u91cd\u8981\u7684\u4e00\u4e2a\u7406\u5ff5\u3002\u4e92\u8054\u7f51\u7ecf\u5e38\u8bf4\u8fed\u4ee3\u4ea7\u54c1\uff0c\u901a\u8fc7\u8fed\u4ee3\u65b9\u5f0f\u4f7f\u4ea7\u54c1\u66f4\u52a0\u5b8c\u5584\uff0c\u8fed\u4ee3\u7b97\u6cd5\u901a\u8fc7\u6b65\u6b65\u903c\u8fd1\u95ee\u9898\u7684\u89e3\uff0c\u6211\u4eec\u5b66\u8bed\u8a00\u7684\u76ee\u7684\u662f\u4e3a\u4e86\u8868\u8fbe\uff0c\u540c\u6837\u5728\u8868\u8fbe\u4e0a\uff0c\u6211\u4eec\u4e5f\u53ef\u4ee5\u901a\u8fc7\u8fed\u4ee3\u65b9\u5f0f\uff0c\u6b65\u6b65\u903c\u8fd1\u8868\u8fbe\u66f4\u6e05\u6670\u3001\u51c6\u786e\u4e14\u6709\u903b\u8f91\u7684\u76ee\u6807"}]}')}}]);